export class TableTop {
    constructor() {
        this.grid = [
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null]
        ]
    }
    placeRobot(x, y) {
        this.grid = this.grid.map(row => {
            row.map(col => "X")
        })
    }
}

export class Robot {
    constructor() {
        this.location = null
    }

    place() {
        this.location = [2, 2]
    }
}
