import { useEffect, useState } from 'react';
import './App.css';
import Board from './components/Board'
import Controls from './components/Controls';

const defaultGrid = [
  [false, false, false, false, false],
  [false, false, false, false, false],
  [false, false, false, false, false],
  [false, false, false, false, false],
  [false, false, false, false, false]
]

function App() {
  const [grid, setGrid] = useState(defaultGrid)
  const [robotFacing, setRobotFacing] = useState(null)

  const init = () => {
    let initialGrid = [...defaultGrid];
    initialGrid[0][0] = true;
    setGrid(initialGrid);
    setRobotFacing("North")
  }

  return (
    <div className="App">
      <aside>
        <Controls 
          init={init}
          grid={grid}
          direction={robotFacing}
        />
      </aside>
      <main>
        <Board
          grid={grid}
        />
      </main>


    </div>
  );
}

export default App;
