import { useEffect, useState } from 'react';
import './App.css';
import Board from './components/Board'
import Controls from './components/Controls';

const defaultGrid = [
  [false, false, false, false, false],
  [false, false, false, false, false],
  [false, false, false, false, false],
  [false, false, false, false, false],
  [false, false, false, false, false]
]

function App() {
  const [grid, setGrid] = useState(defaultGrid)
  const [robot, setRobot] = useState(null)

  useEffect(() => {
    console.log(robot);
    console.log(grid);
  }, [robot])

  const init = () => {
    // let initialGrid = [...defaultGrid];
    // initialGrid[0][0] = true;
    // setGrid(initialGrid);
    setRobot({
      facing: "North",
      location: [0, 0]
    })
  }

  return (
    <div className="App">
      <aside>
        <Controls 
          init={init}
          grid={grid}
          robot={robot}
        />
      </aside>
      <main>
        <Board
          grid={grid}
          robot={robot}
        />
      </main>


    </div>
  );
}

export default App;
