import Cell from "./Cell"

export default function Board({ grid, robot }) {
  const checkForRobot = (x, y) => {
    if (!robot) {
      return false
    } else if (x === robot.location[0] && y === robot.location[1]) {
      return true
    } else {
       return false
    }
  }

  return (
    <div className="Board">
       { [...grid].map((row, index1) => (
          <div className="row" key={index1}>
              {row.map((cell, index2) => (
              <Cell 
                key={`${index1}, ${index2}`} 
                robot={checkForRobot(index1, index2)}
                // {robot.location[0] == index1 && robot.location[1] == index2 ? true : false} 
              /> 
              ))}
          </div> 
        ))}
    </div>
  )
}
