export class TableTop {
    constructor() {
        this.defaultGrid = [
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null]
        ]
        this.grid = []
    }
    placeRobot(row, col) {
        let newGrid = [...this.defaultGrid]
        newGrid[row][col] = "X"
        this.grid = newGrid
    }
}

export class Robot {
    constructor() {
        this.location = null
    }

    place() {
        this.location = [2, 2]
    }
}
