export class TableTop {
    constructor() {
        this.grid = [
            [false, false, false, false, false],
            [false, false, false, false, false],
            [false, false, false, false, false],
            [false, false, false, false, false],
            [false, false, false, false, false]
        ]
    }
    placeRobot(x, y) {
        let newGrid = [...this.grid]
        newGrid[x][y] = true
    }
}

export class Robot {
    constructor() {
        this.location = null
    }

    place() {
        this.location = [2, 2]
    }
}
