export default function Controls({ setGrid, tableTop }) {

  const place = () => {
    setGrid(previousGrid => {
      previousGrid.map(row => {
        row.map(col => true)
      })
    })
  }

  return (
    <div className="Controls">

      <div className="place-wrapper">
        <button onClick={place}>Place robot</button>
        </div>
      <button>move</button>
      <button>left</button>
      <button>right</button>
      <button>report</button>

      <p className="report-output">
      </p>

    </div>
  )
}
