export class TableTop {
    constructor() {
        this.grid = [
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null],
            [null, null, null, null, null]
        ]
    }
    placeRobot(a, b) {
        this.grid.map((row, rowIndex) => {
            row.map((col, colIndex) => {
                if (rowIndex === a && colIndex === b) {
                    return "X"
                } else {
                    return null
                }
            })
        })
        console.log(this.grid);
    }
}

export class Robot {
    constructor() {
        this.location = null
    }

    place() {
        this.location = [2, 2]
    }
}
