import { useEffect, useState } from 'react';
import './App.css';
import Board from './components/Board'
import Controls from './components/Controls';
import { TableTop, Robot } from './utils/Classes';

const tableTop = new TableTop()

function App() {
  const [grid, setGrid] = useState(tableTop.grid)

  useEffect(() => {
    console.log(tableTop.grid);
  }, [grid])

  return (
    <div className="App">
      <aside>
        <Controls 
          setGrid={setGrid}
          tableTop={tableTop}
        />
      </aside>
      <main>
        <Board
          grid={grid}
        />
      </main>


    </div>
  );
}

export default App;
