export class TableTop {
    constructor() {
        this.defaultGrid = [
            [false, false, false, false, false],
            [false, false, false, false, false],
            [false, false, false, false, false],
            [false, false, false, false, false],
            [false, false, false, false, false]
        ]
        this.grid = Array.from(this.defaultGrid)
    }
    placeRobot(x, y) {
        let newGrid = Array.from(this.defaultGrid)
        console.log(newGrid);
        newGrid[x][y] = true
        this.grid = newGrid
    }
}

export class Robot {
    constructor() {
        this.location = null
    }

    place() {
        this.location = [2, 2]
    }
}
